local Window = Library:CreateWindow({
    Title = 'Counter Blox Remastered',
    Center = true, 
    AutoShow = true,
})

local Tabs = {
    Aim = Window:AddTab('Aimbot'), 
    Visuals = Window:AddTab('Visuals'),
    Rage = Window:AddTab('Rage'),
	World = Window:AddTab('World'),
	Character = Window:AddTab('Character'),
	Camera = Window:AddTab('Camera'),
	Misc = Window:AddTab('Misc'),
}

local LockFOV = false
local FOvv = 70

local tPsn = false

local ArmChams = false
local ArmChamsTrans = 90

local Bhop = false
local BhopSpeed = 8

--SCRIPTS

game["Run Service"].RenderStepped:Connect(function()
	ClC.TintColor = Color3.fromRGB(cR, cG, cB)
	if tPsn then
		workspace.Camera.CFrame *= CFrame.new(1,0,10)
		workspace.Camera.CameraSubject = game.Players.LocalPlayer.Character.Head
	else
		workspace.Camera.CameraSubject = game.Players.LocalPlayer.Character.Humanoid
	end
    if LockFOV then
		workspace.Camera.FieldOfView = FOvv
    end
	--Bhop velocity
	if game:GetService("UserInputService"):IsKeyDown(Enum.KeyCode.Space) and Bhop then
		local speed = BhopSpeed*10
		local dir = game.Workspace.CurrentCamera.CFrame.LookVector * Vector3.new(1,0,1)
		local moov = Vector3.new()
		moov = game:GetService("UserInputService"):IsKeyDown(Enum.KeyCode.W) and moov + dir or moov
		moov = game:GetService("UserInputService"):IsKeyDown(Enum.KeyCode.S) and moov - dir or moov
		moov = game:GetService("UserInputService"):IsKeyDown(Enum.KeyCode.D) and moov + Vector3.new(-dir.Z,0,dir.X) or moov
		moov = game:GetService("UserInputService"):IsKeyDown(Enum.KeyCode.A) and moov + Vector3.new(dir.Z,0,-dir.X) or moov
		if moov.Unit.X == moov.Unit.X then
       		moov = moov.Unit
      		game.Players.LocalPlayer.Character.HumanoidRootPart.Velocity = Vector3.new(moov.X*speed,game.Players.LocalPlayer.Character.HumanoidRootPart.Velocity.Y,moov.Z*speed)
		end
	end
--Arm Chams
if ArmChams and workspace.Camera:FindFirstChild("Arms") then
local aP = workspace.Camera:FindFirstChild("Arms"):GetDescendants()

for _,i in pairs(aP) do
	if i:IsA("BasePart") then
		i.Transparency = ArmChamsTrans/100
		i.Color = Color3.new(1, 0.447059, 0.952941)
		i.Material = Enum.Material.ForceField
	end
	if i:IsA("SpecialMesh") then
		i.TextureId = ""
	end
end
elseif not ArmChams then
local aP = workspace.Camera:FindFirstChild("Arms"):GetDescendants()

for _,i in pairs(aP) do
	if i:IsA("BasePart") then
